mapper.select:
SELECT tpt.text_write_seq, tpt.paper_text, tpt.create_date, tu.nick_name FROM tb_paper tp LEFT OUTER JOIN tb_paper_text tpt ON tp.paper_seq = tpt.paper_seq LEFT OUTER JOIN tb_user tu on tpt.create_user_seq = tu.user_seq WHERE tpt.paper_seq = #{paperSeq}
AND tpt.create_user_seq = #{createUserSeq}
ORDER BY tpt.create_date ASC

mapper.update:
UPDATE tb_paper
SET like_cnt = like_cnt + 1
WHERE paper_seq = #{paperSeq}
